# --- 5. FUNGSI DATA ---
@st.cache_data(ttl=60, show_spinner=False) # TTL singkat khusus intraday agar data tetap fresh
def get_intraday_data(tickers):
    if not tickers: return pd.DataFrame()
    try:
        # Menggunakan 1d period dan 1m interval agar grafik mulus
        d = yf.download(tickers, period="1d", interval="1m", group_by='ticker', progress=False, auto_adjust=True)
        return d
    except: return pd.DataFrame()

@st.cache_data(ttl=600, show_spinner=False)
def get_data(tickers, period="3mo", interval="1d"):
    if not tickers: return pd.DataFrame()
    try:
        d = yf.download(tickers, period=period, interval=interval, group_by='ticker', progress=False, auto_adjust=True)
        return d
    except: return pd.DataFrame()

def fmt_idr(val):
    return f"{val:,.0f}" if not pd.isna(val) else "0"

# --- 6. VISUALISASI ---
def chart_intraday(df, ticker):
    fig = go.Figure()
    # Area chart untuk efek mulus
    fig.add_trace(go.Scatter(
        x=df.index, y=df['Close'], 
        mode='lines', 
        line=dict(color='#00C805' if df['Close'].iloc[-1] >= df['Open'].iloc[0] else '#FF333A', width=2),
        fill='tozeroy',
        fillcolor='rgba(0, 200, 5, 0.1)' if df['Close'].iloc[-1] >= df['Open'].iloc[0] else 'rgba(255, 51, 58, 0.1)'
    ))
    
    fig.update_layout(
        title=f"<b>{ticker}</b> | {fmt_idr(df['Close'].iloc[-1])} ({((df['Close'].iloc[-1]-df['Open'].iloc[0])/df['Open'].iloc[0]*100):+.2f}%)",
        margin=dict(l=10, r=10, t=40, b=10),
        height=300,
        template="plotly_dark",
        xaxis=dict(showgrid=False, title="Time"),
        yaxis=dict(side="right", showgrid=True, gridcolor='rgba(255,255,255,0.1)')
    )
    return fig

