# === TAB BARU: MULTI-SEARCH ===
with tabs[0]:
    st.subheader("Multi-Stock Intraday Viewer")
    input_saham = st.text_input("Ketik kode saham (pisahkan dengan spasi atau koma):", 
                               placeholder="Contoh: bbca bbri goto bren",
                               key="search_input").upper()
    
    if input_saham:
        # Parsing input menjadi list ticker
        symbols = [s.strip() for s in input_saham.replace(',', ' ').split() if s.strip()]
        formatted_symbols = [s if s.endswith(".JK") else f"{s}.JK" for s in symbols]
        
        with st.spinner("Fetching Intraday Data..."):
            df_intraday = get_intraday_data(formatted_symbols)
            
        if not df_intraday.empty:
            # Layout grid 2 kolom untuk grafik intraday
            cols_search = st.columns(2)
            for i, t in enumerate(formatted_symbols):
                try:
                    # Ambil data per ticker dari hasil download batch
                    if len(formatted_symbols) > 1:
                        if t not in df_intraday.columns.levels[0]: continue
                        dft = df_intraday[t].dropna()
                    else:
                        dft = df_intraday.dropna()
                    
                    if dft.empty: continue
                    
                    with cols_search[i % 2]:
                        st.plotly_chart(chart_intraday(dft, t), use_container_width=True)
                except Exception as e:
                    continue
        else:
            st.warning("Data tidak ditemukan. Pastikan kode saham benar.")

